$('#prod_search').bind("keypress", function(e) {
    //alert("search");
    if (e.keyCode == 13) {
        var url = $('base').attr('href') + 'index.php?route=product/search';

        var value = $('#prod_search').val();

        if (value) {
            url += '&search=' + encodeURIComponent(value);
        }

        location = url;
    }
});

$('#submit_search').on('click', function(e) {
    var url = $('base').attr('href') + 'index.php?route=product/search';

    var value = $('#prod_search').val();

    if (value) {
        url += '&search=' + encodeURIComponent(value);
    }

    location = url;
});

